// ACTION TYPES

const ADD_TASK = "ADD_TASK"
const REMOVE_TASK = "REMOVE_TASK"


// ADD_TASK(id, title)
       const addTask = (id, title) => ({
    type: ADD_TASK,
    payload: {
        title,
        id
    }
})

// REMOVE_TASK(id)
       const removeTask = (id) => ({
    type: REMOVE_TASK,
    payload: id
    
})

// REDUCER
const tasksReducer = (tasks = [], action) => {
    switch(action.type) {
        case ADD_TASK:
            return [...tasks, action.payload]
        case REMOVE_TASK:
            return tasks.filter(item => item.id !== action.payload)
        default:
            return tasks
    }
}


